function renderChart(point) {
    Highcharts.chart('hc-tooltip', {
        chart: { type: 'pie' },
        title: { text: 'Product Category Breakdown' },
        series: [{
            data: point.options.eData,
            dataLabels: { enabled: false }
        }]
    });
}

Highcharts.addEvent(Highcharts.Tooltip, 'refresh', function() {
    renderChart(this.chart.hoverPoint);
});

Highcharts.chart('container', {
    chart: { type: 'line', margin: [40, 80, 80, 80] },
    title: { text: '2023 Monthly Sales Trends', style: { fontSize: '20px', color: '#2c3e50' } },
    xAxis: {
        type: 'datetime',
        labels: { format: '{value:%b}', style: { color: '#7f8c8d' } },
    yAxis: {
        title: { text: 'Sales (kUSD)', style: { color: '#7f8c8d' } },
        labels: { format: '{value}k' }
    },
    tooltip: {
        useHTML: true,
        headerFormat: '<small>{point.x:%B}</small><br>',
        pointFormat: '<div id="hc-tooltip"></div>'
    },
    series: [{
        name: 'Total Sales',
        data: [
            { x: Date.UTC(2023,0), y: 124, eData: generateCategories(45,30,25) },
            { x: Date.UTC(2023,1), y: 138, eData: generateCategories(42,33,25) },
            { x: Date.UTC(2023,2), y: 155, eData: generateCategories(40,35,25) },
            { x: Date.UTC(2023,3), y: 161, eData: generateCategories(38,37,25) },
            { x: Date.UTC(2023,4), y: 172, eData: generateCategories(35,40,25) },
            { x: Date.UTC(2023,5), y: 185, eData: generateCategories(32,43,25) },
            { x: Date.UTC(2023,6), y: 193, eData: generateCategories(30,45,25) },
            { x: Date.UTC(2023,7), y: 204, eData: generateCategories(28,47,25) },
            { x: Date.UTC(2023,8), y: 212, eData: generateCategories(25,50,25) },
            { x: Date.UTC(2023,9), y: 218, eData: generateCategories(22,53,25) },
            { x: Date.UTC(2023,10), y: 227, eData: generateCategories(20,55,25) },
            { x: Date.UTC(2023,11), y: 235, eData: generateCategories(18,57,25) }
        ]
    }],
    colors: ['#3498db', '#e74c3c', '#2ecc71'],
    legend: { align: 'right', verticalAlign: 'top' }
});

function generateCategories(main, secondary, other) {
    return [
        { name: 'Electronics', y: main },
        { name: 'Apparel', y: secondary },
        { name: 'Home Goods', y: other }
    ];
}
