visualization_code:
Highcharts.chart('container', {
    chart: {
        type: 'heatmap',
        marginTop: 80,
        marginBottom: 40,
        plotBorderWidth: 1
    },
    title: {
        text: 'How people with color blindness see colors'
    },
    xAxis: {
        categories: ['Red', 'Blue', 'Yellow', 'Green'],
        labels: { style: { fontSize: '14px', color: '#333' } }
    },
    yAxis: {
        categories: ['Normal vision', 'Deuteranomalia', 'Protanopia', 'Tritanopia'],
        title: null,
        labels: { style: { fontSize: '14px', color: '#333' } }
    },
    colorAxis: {},
    legend: { enabled: false },
    tooltip: {
        format: 'How people with <b>{series.yAxis.categories.(point.y)}</b> see <b>{series.xAxis.categories.(point.x)}</b>'
    },
    plotOptions: {
        series: {
            states: { hover: { enabled: false } },
            dataLabels: { enabled: false }
        }
    },
    series: [{
        name: 'Color Perception',
        borderWidth: 1,
        data: [
            {x:0,y:0,color:'red'}, {x:0,y:1,color:'#b65b0a'}, {x:0,y:2,color:'#877a2c'}, {x:0,y:3,color:'#ea3535'},
            {x:1,y:0,color:'blue'}, {x:1,y:1,color:'#0e4287'}, {x:1,y:2,color:'#00428e'}, {x:1,y:3,color:'#004d52'},
            {x:2,y:0,color:'yellow'}, {x:2,y:1,color:'#fae684'}, {x:2,y:2,color:'#fde87c'}, {x:2,y:3,color:'#fddeea'},
            {x:3,y:0,color:'green'}, {x:3,y:1,color:'#596a5f'}, {x:3,y:2,color:'#6d6756'}, {x:3,y:3,color:'#346f79'}
        ]
    }]
});
