#container {
    max-width: 800px;
    height: 400px;
    margin: 1em auto;
}

.highcharts-demo-button {
    background: #2caffe;
    color: white;
    border-radius: 20px;
    padding: 0.8rem 2rem;
    font-family: 'Verdana', sans-serif;
}

visualization_code.JavaScript:
const chart = Highcharts.chart('container', {
    chart: {
        type: 'spline',
        backgroundColor: '#f8f9fa'
    },
    title: {
        text: 'Quarterly Revenue Growth (2023-2024)',
        style: {
            color: '#2c3e50',
            fontSize: '20px'
        }
    },
    xAxis: {
        type: 'datetime',
        labels: {
            format: '{value:%b %Y}',
            style: {
                color: '#7f8c8d'
            }
        }
    },
    yAxis: {
        title: {
            text: 'Revenue (M USD)',
            style: {
                color: '#7f8c8d'
            }
        },
        labels: {
            format: '${value:.1f}M'
        }
    },
    series: [{
        name: 'Actual Revenue',
        data: [
            [Date.UTC(2023, 0), 4.2],
            [Date.UTC(2023, 3), 4.8],
            [Date.UTC(2023, 6), 5.1],
            [Date.UTC(2023, 9), 5.6],
            [Date.UTC(2024, 0), 5.9],
            [Date.UTC(2024, 3), 6.3]
        ],
        color: '#2caffe',
        marker: {
            symbol: 'circle',
            radius: 5
        }
    }],
    plotOptions: {
        spline: {
            dashStyle: 'ShortDot'
        }
    }
});

document.getElementById('add').onclick = function() {
    const lastDate = chart.series[0].data[chart.series[0].data.length - 1].x;
    const newValue = 6.3 + (Math.random() * 0.4 - 0.2);
    chart.series[0].addPoint([lastDate + 7776000000, newValue]);
};
