(async () => {
    const usdeur = await fetch('https://www.highcharts.com/samples/data/usdeur.json').then(r => r.json());

    Highcharts.stockChart('container', {
        chart: { type: 'line' },
        rangeSelector: { selected: 1 },
        title: { 
            text: 'USD to EUR Exchange Rate',
            style: { color: '#2c3e50', fontSize: '18px' }
        },
        xAxis: {
            maxZoom: 14 * 24 * 3600000,
            labels: { style: { color: '#7f8c8d' } }
        },
        yAxis: {
            title: { 
                text: 'Rate',
                style: { color: '#7f8c8d' }
            },
            labels: { style: { color: '#7f8c8d' } }
        },
        series: [{
            name: 'Daily Rate',
            data: usdeur,
            color: '#3498db',
            tooltip: { yDecimals: 3 }
        }],
        plotOptions: {
            series: {
                marker: { enabled: false }
            }
        }
    });
})();
