Highcharts.chart('container', {
    chart: {
        type: 'bubble',
        zooming: { type: 'xy' },
        backgroundColor: '#ffffff'
    },
    title: {
        text: 'Synthetic Data Density Distribution',
        style: { fontSize: '18px', color: '#333333' }
    },
    xAxis: {
        gridLineWidth: 1,
        title: { text: 'X Value', style: { color: '#666666' } },
        labels: { style: { color: '#444444' } }
    },
    yAxis: {
        title: { text: 'Y Value', style: { color: '#666666' } },
        labels: { style: { color: '#444444' } }
    },
    legend: { enabled: false },
    boost: {
        useGPUTranslations: true,
        usePreallocated: true,
        enabled: true
    },
    series: [{
        name: 'Data Density',
        boostBlending: 'alpha',
        color: 'rgba(152, 0, 67, 0.1)',
        data: (function() {
            const data = [];
            for (let i = 0; i < 50000; i++) {
                data.push([
                    Math.pow(Math.random(), 2) * 100,
                    Math.pow(Math.random(), 2) * 100,
                    Math.pow(Math.random(), 2) * 100
                ]);
            }
            return data;
        })(),
        minSize: 1,
        maxSize: 10,
        tooltip: {
            pointFormat: 'Coordinate: [{point.x:.1f}, {point.y:.1f}]<br>Magnitude: {point.z:.1f}'
        }
    }]
});
