visualization_code.CSS:
#container {
  min-width: 310px;
  height: 500px;
  margin: 0 auto;
}

.highcharts-figure {
  max-width: 800px;
  margin: 2em auto;
}

.highcharts-title {
  font-size: 1.4em !important;
  fill: #2c3e50 !important;
}

visualization_code.JavaScript:
Highcharts.chart('container', {
  chart: {
    type: 'spline',
    backgroundColor: '#f9f9f9'
  },
  title: {
    text: 'Monthly Renewable Energy Production (2023)',
    style: {
      fontSize: '20px',
      fontWeight: 'bold'
    }
  },
  xAxis: {
    type: 'datetime',
    labels: {
      format: '{value:%b}',
      style: {
        color: '#7f8c8d'
      }
    },
    gridLineWidth: 1,
    gridLineColor: '#ecf0f1'
  },
  yAxis: {
    title: {
      text: 'Megawatt Hours (MWh)',
      style: {
        color: '#7f8c8d'
      }
    },
    labels: {
      format: '{value:,f}',
      style: {
        color: '#7f8c8d'
      }
    }
  },
  plotOptions: {
    spline: {
      marker: {
        enabled: false
      }
    }
  },
  series: [{
    name: 'Solar Production',
    data: [
      [Date.UTC(2023,0), 12500],
      [Date.UTC(2023,1), 14200],
      [Date.UTC(2023,2), 16800],
      [Date.UTC(2023,3), 19500],
      [Date.UTC(2023,4), 21000],
      [Date.UTC(2023,5), 23500],
      [Date.UTC(2023,6), 24500],
      [Date.UTC(2023,7), 23000],
      [Date.UTC(2023,8), 20500],
      [Date.UTC(2023,9), 17500],
      [Date.UTC(2023,10), 14000],
      [Date.UTC(2023,11), 12800]
    ],
    color: '#e67e22'
  }, {
    name: 'Wind Production',
    data: [
      [Date.UTC(2023,0), 18200],
      [Date.UTC(2023,1), 19500],
      [Date.UTC(2023,2), 17800],
      [Date.UTC(2023,3), 16200],
      [Date.UTC(2023,4), 15500],
      [Date.UTC(2023,5), 14800],
      [Date.UTC(2023,6), 14200],
      [Date.UTC(2023,7), 15800],
      [Date.UTC(2023,8), 17500],
      [Date.UTC(2023,9), 19200],
      [Date.UTC(2023,10), 20800],
      [Date.UTC(2023,11), 22000]
    ],
    color: '#3498db'
  }],
  legend: {
    layout: 'horizontal',
    align: 'center',
    verticalAlign: 'bottom'
  },
  tooltip: {
    headerFormat: '<b>{point.x:%B %Y}</b><br>',
    pointFormat: '{series.name}: {point.y:,.0f} MWh'
  }
});
