let data = [], lastPoint, totalDistance = 0;

Array.from(xml.getElementsByTagName('trkpt')).reverse().forEach((trkpt, i) => {
  const ele = parseInt(trkpt.getElementsByTagName('ele')[0].textContent, 10);
  const lat = parseFloat(trkpt.getAttribute('lat'));
  const lon = parseFloat(trkpt.getAttribute('lon'));
  totalDistance += lastPoint ? getDistance(lastPoint, {lat, lon}) : 0;
  data.push({ x: Math.round(totalDistance * 100)/100, y: ele-15, ... });
  lastPoint = {lat, lon};
});

