visualization_code.JavaScript:
Highcharts.chart('container', {
    chart: {
        type: 'scatter',
        zooming: { type: 'x' },
        backgroundColor: '#FFFFFF',
        borderColor: '#333333',
        borderRadius: 8,
        margin: [50, 80, 80, 80]
    },
    title: {
        text: 'Measured vs Expected Data',
        style: { fontSize: '20px', color: '#2F2F2F' }
    },
    subtitle: {
        text: 'y = sin(x)',
        style: { color: '#606060' }
    },
    xAxis: {
        title: {
            text: 'Phase Angle (radians)',
            style: { fontSize: '14px', color: '#404040' }
        },
        labels: { style: { color: '#606060' } },
        gridLineColor: '#F0F0F0'
    },
    yAxis: {
        title: {
            text: 'Amplitude',
            style: { fontSize: '14px', color: '#404040' }
        },
        labels: { style: { color: '#606060' } },
        gridLineColor: '#F0F0F0'
    },
    legend: {
        align: 'right',
        verticalAlign: 'top',
        itemStyle: { color: '#333333' }
    },
    plotOptions: {
        scatter: {
            marker: { radius: 1.5, fillColor: 'rgba(65,105,225,0.4)' }
        },
        functionseries: {
            color: '#B22222',
            lineWidth: 2
        }
    },
    series: [{
        type: 'scatter',
        name: 'Measured (μ=0, σ=0.29)',
        data: Array.from({length: 1000}, (_,i) => [i/10, Math.sin(i/10) + (Math.random()-0.5)])
    },{
        type: 'functionseries',
        name: 'Expected (sin(x))',
        min: 0,
        max: 100,
        dataFunction: x => Math.sin(x/10)
    }]
});
