visualization_code.JavaScript:
Highcharts.chart('container', {
    chart: {
        type: 'item',
        plotBorderWidth: 1
    },
    title: {
        text: 'Synthetic Parliament Composition 2023',
        style: { fontSize: '20px', color: '#333'}
    },
    series: [{
        name: 'Parties',
        keys: ['name', 'y', 'color', 'label'],
        data: [
            ['Progressive Alliance', 85, '#FF6B6B', 'PROG'],
            ['Social Democrats', 142, '#EB001F', 'SD'],
            ['Green Coalition', 98, '#4CAF50', 'GREENS'],
            ['Market Liberals', 75, '#FFD700', 'LIB'],
            ['Conservative Union', 165, '#2F4F4F', 'CU'],
            ['Regional Bloc', 45, '#1E90FF', 'REGION'],
            ['Digital Future', 60, '#9C27B0', 'DIGI'],
            ['Labor Front', 110, '#D32F2F', 'LABOR'],
            ['Centrist Alliance', 88, '#808080', 'CENT'],
            ['Eco-Socialists', 55, '#8BC34A', 'ECO'],
            ['Law & Order', 72, '#3F51B5', 'LAW'],
            ['Pensioners Party', 48, '#FF9800', 'PENS']
        ],
        dataLabels: { enabled: true, format: '{point.label}' },
        center: ['50%', '88%'],
        size: '170%',
        startAngle: -100,
        endAngle: 100,
        itemSize: 24,
        pointPadding: 0.8
    }],
    plotOptions: {
        item: {
            radialGradient: {
                cx: 0.5,
                cy: 0.5,
                r: 0.8
            },
            style: {
                textOutline: 'none'
            }
        }
    }
});
