visualization_code.JavaScript:
Highcharts.chart('container', {
    chart: {
        type: 'area',
        inverted: true
    },
    title: {
        text: 'Inverted Area Chart with X-Axis Zones',
        align: 'left'
    },
    xAxis: {
        reversed: false
    },
    yAxis: {
        title: null,
        reversed: false
    },
    plotOptions: {
        series: {
            marker: { enabled: false },
            zoneAxis: 'x',
            lineWidth: 5
        }
    },
    series: [{
        data: [1, 2, 0.5, 3, 1, 0.1, 3, 1],
        color: 'lightgreen',
        zones: [{
            value: 1,
            color: 'red'
        }, {
            value: 2,
            color: 'blue'
        }]
    }],
    legend: { enabled: false },
    accessibility: { enabled: false }
});
