visualization_code.CSS:
* {
    font-family: Helvetica, Arial, sans-serif;
}

#outer-container {
    margin-right: 95px;
    margin-top: 170px;
    margin-bottom: 80px;
}

#scaling-container {
    transform: scale(0.6, 1.2);
}

#container {
    height: 400px;
    max-width: 700px;
}

visualization_code.JavaScript:
Highcharts.chart('container', {
    chart: {
        type: 'line',
        backgroundColor: '#FFFFFF',
        marginRight: 95,
        marginTop: 50
    },
    title: {
        text: 'Monthly Performance Trends (2023-2024)',
        style: {
            fontSize: '18px',
            color: '#333333'
        }
    },
    xAxis: {
        type: 'datetime',
        labels: {
            format: '{value:%b %Y}',
            style: {
                color: '#666666',
                fontSize: '12px'
            }
        }
    },
    yAxis: {
        title: {
            text: 'Metric Value',
            style: {
                color: '#666666',
                fontSize: '12px'
            }
        },
        labels: {
            style: {
                color: '#666666',
                fontSize: '12px'
            }
        }
    },
    tooltip: {
        outside: true,
        useHTML: true,
        headerFormat: '<b>{point.key:%B %Y}</b><br>',
        pointFormat: '{series.name}: {point.y}'
    },
    series: [{
        name: 'Product Sales (Units)',
        data: [
            [Date.UTC(2023,0), 125],
            [Date.UTC(2023,1), 138],
            [Date.UTC(2023,2), 142],
            [Date.UTC(2023,3), 156],
            [Date.UTC(2023,4), 163],
            [Date.UTC(2023,5), 175],
            [Date.UTC(2023,6), 168],
            [Date.UTC(2023,7), 159],
            [Date.UTC(2023,8), 172],
            [Date.UTC(2023,9), 185],
            [Date.UTC(2023,10), 193],
            [Date.UTC(2023,11), 207],
            [Date.UTC(2024,0), 215],
            [Date.UTC(2024,1), 209],
            [Date.UTC(2024,2), 221]
        ],
        color: '#2E8B57',
        type: 'line'
    }, {
        name: 'Customer Satisfaction Index',
        data: [
            [Date.UTC(2023,0), 4.2],
            [Date.UTC(2023,1), 4.5],
            [Date.UTC(2023,2), 4.3],
            [Date.UTC(2023,3), 4.7],
            [Date.UTC(2023,4), 4.6],
            [Date.UTC(2023,5), 4.8],
            [Date.UTC(2023,6), 4.4],
            [Date.UTC(2023,7), 4.5],
            [Date.UTC(2023,8), 4.9],
            [Date.UTC(2023,9), 5.0],
            [Date.UTC(2023,10), 5.1],
            [Date.UTC(2023,11), 5.2],
            [Date.UTC(2024,0), 5.3],
            [Date.UTC(2024,1), 5.1],
            [Date.UTC(2024,2), 5.4]
        ],
        color: '#4682B4',
        type: 'spline'
    }],
    legend: {
        layout: 'horizontal',
        align: 'center',
        verticalAlign: 'bottom',
        itemStyle: {
            color: '#333333',
            fontSize: '12px'
        }
    }
});
