visualization_code.JavaScript:
Highcharts.chart('container', {
    chart: { type: 'line' },
    title: { text: 'Monthly Sales Performance 2023', style: { color: '#2c3e50', fontSize: '20px' } },
    xAxis: {
        categories: ['Jan','Feb','Mar','Apr','May','Jun','Jul','Aug','Sep','Oct','Nov','Dec'],
        labels: { style: { color: '#7f8c8d', fontWeight: 'bold' } }
    },
    yAxis: {
        title: { text: 'Revenue (USD thousands)', style: { color: '#7f8c8d' } },
        labels: { format: '{value}k', style: { color: '#7f8c8d' } }
    },
    tooltip: { shadow: false, valueSuffix: 'k USD' },
    series: [{
        name: 'Enterprise Solutions',
        data: [85, 78, 112, 129, 144, 176, 135, 148, 216, 194, 158, 204],
        color: '#3498db'
    }, {
        name: 'Consumer Products',
        data: [194, 156, 124, 89, 115, 106, 129, 144, 135, 176, 148, 95],
        color: '#e74c3c'
    }],
    legend: {
        align: 'right',
        verticalAlign: 'top',
        itemStyle: { color: '#34495e' }
    }
});
