visualization_code:
Highcharts.chart('container', {
    chart: {
        type: 'spline',
        backgroundColor: '#ffffff',
        borderColor: '#cccccc',
        borderRadius: 5,
        borderWidth: 1
    },
    title: {
        text: 'Monthly Sales Trends (2010)',
        style: { color: '#333333', fontSize: '20px' }
    },
    xAxis: {
        type: 'datetime',
        labels: {
            rotation: -45,
            style: { color: '#666666', fontSize: '12px' }
        },
        title: {
            text: 'Month',
            style: { color: '#333333', fontSize: '14px' }
        }
    },
    yAxis: {
        title: {
            text: 'Sales (USD)',
            style: { color: '#333333', fontSize: '14px' }
        },
        labels: {
            style: { color: '#666666', fontSize: '12px' }
        }
    },
    series: [{
        name: 'Sales',
        data: [
            [Date.UTC(2010,0), 85],
            [Date.UTC(2010,1), 112],
            [Date.UTC(2010,2), 134],
            [Date.UTC(2010,3), 158],
            [Date.UTC(2010,4), 192],
            [Date.UTC(2010,5), 225],
            [Date.UTC(2010,6), 203],
            [Date.UTC(2010,7), 187],
            [Date.UTC(2010,8), 164],
            [Date.UTC(2010,9), 142],
            [Date.UTC(2010,10), 118],
            [Date.UTC(2010,11), 96]
        ],
        color: '#4a90e2',
        marker: { enabled: false }
    }],
    legend: {
        enabled: true,
        align: 'right',
        verticalAlign: 'top'
    },
    tooltip: {
        headerFormat: '<b>{point.x:%B %Y}</b><br>',
        pointFormat: 'Sales: ${point.y:.1f}K'
    }
});
