visualization_code.JavaScript:
Highcharts.chart('container', {
    chart: {
        type: 'column',
        backgroundColor: 'transparent'
    },
    title: {
        text: 'Product Category Sales Q1 2024',
        style: {
            color: '#2c3e50',
            fontSize: '20px',
            fontWeight: 'bold'
        }
    },
    xAxis: {
        categories: ['Pharma', 'Biotech', 'Neuroscience', 'Genomics', 'Diagnostics',
            'Bioinformatics', 'CRISPR', 'Proteomics', 'Synthetic Bio', 'Biomaterials'],
        labels: {
            style: {
                color: '#34495e',
                fontSize: '12px'
            }
        },
        lineColor: '#bdc3c7'
    },
    yAxis: {
        title: {
            text: 'Revenue (M USD)',
            style: {
                color: '#34495e',
                fontSize: '14px'
            }
        },
        labels: {
            format: '{value:.1f}',
            style: {
                color: '#7f8c8d'
            }
        },
        gridLineColor: '#ecf0f1'
    },
    series: [{
        name: 'Quarterly Sales',
        data: [
            {y: 45.2, color: '#00FF00'},
            {y: 68.7, color: '#2FE62F'},
            {y: 52.1, color: '#5FCC5F'},
            {y: 79.4, color: '#8FB38F'},
            {y: 61.8, color: '#BF99BF'},
            {y: 57.3, color: '#DF80DF'},
            {y: 83.6, color: '#F766F7'},
            {y: 49.5, color: '#FF4DFF'},
            {y: 72.0, color: '#FF33FF'},
            {y: 65.9, color: '#FF00FF'}
        ],
        dataLabels: {
            enabled: true,
            format: '${point.y:.1f}M',
            color: '#2c3e50'
        }
    }],
    plotOptions: {
        column: {
            pointPadding: 0.15,
            borderWidth: 0,
            borderRadius: 4
        }
    },
    legend: {
        align: 'right',
        verticalAlign: 'top',
        itemStyle: {
            color: '#34495e'
        }
    }
});
