visualization_code:
Highcharts.chart('container', {
  colors: ['#bfdbf6', '#92c2ef', '#10487F'],
  chart: {
    type: 'pie'
  },
  title: {
    text: 'Air composition',
    style: {
      fontSize: '18px',
      color: '#2a2a2a'
    }
  },
  tooltip: {
    valueSuffix: '%',
    headerFormat: '<span style="font-size: 13px">{point.key}</span><br/>'
  },
  plotOptions: {
    pie: {
      allowPointSelect: true,
      cursor: 'pointer',
      dataLabels: {
        enabled: true,
        format: '{point.name}: {y}%',
        distance: -30,
        style: {
          fontWeight: 'bold',
          color: 'contrast',
          textOutline: 'none'
        }
      },
      innerSize: '75%',
      showInLegend: true
    }
  },
  series: [{
    name: 'Composition',
    colorByPoint: true,
    data: [
      {name: 'Nitrogen', y: 78, color: '#bfdbf6'},
      {name: 'Oxygen', y: 20.9, color: '#92c2ef'},
      {name: 'Argon', y: 0.93, color: '#10487F'},
      {name: 'CO₂', y: 0.04, color: '#5a7fa4'},
      {name: 'Other', y: 0.1, color: '#1e3a5a'}
    ]
  }],
  legend: {
    layout: 'vertical',
    align: 'right',
    verticalAlign: 'middle',
    itemStyle: {
      color: '#606060',
      fontSize: '12px'
    }
  }
});
