// Generate synthetic measured data
const scatterData = [];
for (let i = 0; i < 100; i += 0.1) {
    scatterData.push([i, Math.sin(i/10) + (Math.random() - 0.5)]);
}

// Generate expected sine wave data
const expectedData = [];
for (let x = 0; x <= 100; x += 0.5) {
    expectedData.push([x, Math.sin(x/10)]);
}

Highcharts.chart('container', {
    chart: {
        type: 'line',
        zooming: { type: 'x' },
        backgroundColor: '#FFFFFF'
    },
    title: {
        text: 'Measured vs Expected Data',
        style: { fontSize: '20px', color: '#333333' }
    },
    subtitle: {
        text: 'y = sin(x/10)',
        style: { color: '#666666' }
    },
    xAxis: {
        title: {
            text: 'X Value',
            style: { fontSize: '14px', color: '#333333' }
        },
        labels: { style: { color: '#666666' } }
    },
    yAxis: {
        title: {
            text: 'Y Value',
            style: { fontSize: '14px', color: '#333333' }
        },
        labels: { style: { color: '#666666' } }
    },
    legend: {
        align: 'right',
        verticalAlign: 'top',
        itemStyle: { color: '#333333' }
    },
    series: [{
        type: 'scatter',
        name: 'Measured Data (Noisy)',
        data: scatterData,
        color: 'rgba(30, 144, 255, 0.5)',
        marker: { radius: 2 }
    }, {
        type: 'spline',
        name: 'Expected Function',
        data: expectedData,
        color: '#FF4500',
        lineWidth: 3,
        marker: { enabled: false }
    }]
});
