visualization_code.JavaScript:
Highcharts.chart('container', {
    chart: {
        type: 'area',
        inverted: true
    },
    title: {
        text: 'Value Zones with Axis Configuration',
        style: { fontSize: '18px', color: '#333' }
    },
    xAxis: {
        reversed: false,
        labels: { style: { color: '#666', fontSize: '12px' } }
    },
    yAxis: {
        title: { text: null },
        reversed: false,
        labels: { style: { color: '#666', fontSize: '12px' } }
    },
    plotOptions: {
        area: {
            marker: { enabled: false },
            zoneAxis: 'x',
            lineWidth: 5
        }
    },
    series: [{
        name: 'Threshold Metrics',
        data: [1, 2, 0.5, 3, 1, 0.1, 3, 1],
        color: 'lightgreen',
        zones: [{
            value: 1,
            color: 'red'
        }]
    }],
    legend: { enabled: false }
});
