visualization_code.JavaScript:
Highcharts.chart('container', {
    chart: {
        type: 'line'
    },
    title: {
        text: 'Monthly Performance Comparison',
        style: {
            fontSize: '20px',
            color: '#2c3e50'
        }
    },
    xAxis: {
        categories: ['Jan','Feb','Mar','Apr','May','Jun',
                    'Jul','Aug','Sep','Oct','Nov','Dec'],
        labels: {
            style: {
                color: '#7f8c8d'
            }
        },
        crosshair: true
    },
    yAxis: {
        title: {
            text: 'Metric Value',
            style: {
                color: '#7f8c8d'
            }
        },
        labels: {
            format: '{value:.1f}',
            style: {
                color: '#7f8c8d'
            }
        }
    },
    tooltip: {
        format: '<span style="font-size: 0.8em;color:{series.color}">{series.name}</span><br/>' +
               '<b>{point.y:.1f}</b> ({point.percentage:.1f}%)',
        shared: true
    },
    series: [{
        name: 'Product A',
        data: [29.9,71.5,106.4,129.2,144.0,176.0,135.6,148.5,216.4,194.1,95.6,54.4],
        color: '#3498db'
    },{
        name: 'Product B',
        data: [216.4,194.1,95.6,54.4,29.9,71.5,106.4,129.2,144.0,176.0,135.6,148.5],
        color: '#e74c3c'
    }],
    plotOptions: {
        series: {
            marker: {
                enabled: true,
                radius: 4
            }
        }
    }
});
