visualization_code.JavaScript:
Highcharts.chart('container', {
    chart: {
        type: 'line',
        backgroundColor: '#f9f9f9'
    },
    title: {
        text: 'Monthly Sales Comparison (2023 vs 2024)',
        style: { fontSize: '18px', color: '#2c3e50' }
    },
    xAxis: {
        categories: ['Jan','Feb','Mar','Apr','May','Jun',
                    'Jul','Aug','Sep','Oct','Nov','Dec'],
        labels: { style: { color: '#666', fontSize: '12px' } }
    },
    yAxis: {
        title: { 
            text: 'Sales (USD)',
            style: { color: '#666', fontSize: '12px' }
        },
        labels: { style: { color: '#666', fontSize: '12px' } }
    },
    tooltip: {
        crosshairs: true,
        valueSuffix: ' USD',
        headerFormat: '<b>{point.key}</b><br>'
    },
    legend: {
        layout: 'horizontal',
        align: 'center',
        verticalAlign: 'bottom'
    },
    colors: ['#3498db', '#e67e22'],
    series: [{
        name: '2023 Sales',
        data: [29.9,71.5,106.4,129.2,144.0,176.0,
               135.6,148.5,216.4,194.1,95.6,54.4]
    }, {
        name: '2024 Sales',
        data: [216.4,194.1,95.6,54.4,29.9,71.5,
               106.4,129.2,144.0,176.0,135.6,148.5]
    }]
});
