visualization_code.JavaScript:
Highcharts.chart('container', {
    chart: {
        type: 'line',
        backgroundColor: '#f8f9fa',
        margin: [30, 45, 65, 75]
    },
    title: {
        text: '2023 Product Line Performance',
        style: {
            fontSize: '20px',
            color: '#2c3e50',
            fontWeight: 'bold'
        }
    },
    xAxis: {
        categories: ['Jan','Feb','Mar','Apr','May','Jun','Jul','Aug','Sep','Oct','Nov','Dec'],
        labels: {
            style: {
                color: '#7f8c8d',
                fontSize: '12px'
            }
        },
        lineColor: '#bdc3c7'
    },
    yAxis: {
        title: {
            text: 'Sales (Thousands)',
            style: {
                color: '#7f8c8d',
                fontSize: '14px'
            }
        },
        labels: {
            style: {
                color: '#7f8c8d',
                fontSize: '12px'
            }
        },
        gridLineColor: '#ecf0f1'
    },
    legend: {
        align: 'right',
        verticalAlign: 'top',
        itemStyle: {
            color: '#2c3e50',
            fontSize: '12px'
        }
    },
    series: [{
        name: 'Consumer Electronics',
        data: [82,78,95,115,127,142,155,148,132,118,105,89],
        color: '#3498db',
        marker: {
            symbol: 'circle'
        }
    }, {
        name: 'Industrial Equipment',
        data: [65,68,72,81,89,93,88,85,94,102,110,98],
        color: '#e74c3c',
        marker: {
            symbol: 'square'
        }
    }],
    plotOptions: {
        series: {
            marker: {
                radius: 4
            }
        }
    }
});
