visualization_code.JavaScript:
Highcharts.setOptions({
  time: { timezoneOffset: -4 * 60 }
});

Highcharts.chart('container', {
  chart: {
    type: 'spline',
    backgroundColor: '#FFFFFF',
    borderWidth: 1
  },
  title: {
    text: 'Summer Day Temperature Profile (UTC-4 EDT)',
    style: { fontSize: '18px', color: '#2E3440' }
  },
  xAxis: {
    type: 'datetime',
    labels: {
      format: '{value:%l %p}',
      style: { color: '#4C566A', fontSize: '12px' }
    },
    title: { text: 'Local Time (EDT)', style: { color: '#4C566A' } }
  },
  yAxis: {
    title: {
      text: 'Temperature (°F)',
      style: { color: '#4C566A', fontSize: '12px' }
    },
    labels: { style: { color: '#4C566A', fontSize: '12px' } }
  },
  series: [{
    name: 'Central Park',
    data: [
      [Date.UTC(2024,6,1,4), 68], [Date.UTC(2024,6,1,7), 72],
      [Date.UTC(2024,6,1,10), 78], [Date.UTC(2024,6,1,13), 85],
      [Date.UTC(2024,6,1,16), 89], [Date.UTC(2024,6,1,19), 83],
      [Date.UTC(2024,6,1,22), 75], [Date.UTC(2024,6,2,1), 71],
      [Date.UTC(2024,6,2,4), 69], [Date.UTC(2024,6,2,7), 73],
      [Date.UTC(2024,6,2,10), 80], [Date.UTC(2024,6,2,13), 87]
    ],
    color: '#BF616A',
    marker: { radius: 4 }
  }],
  legend: {
    align: 'center',
    verticalAlign: 'bottom',
    itemStyle: { color: '#4C566A' }
  }
});
