Highcharts.chart('container1', {
  title: {text: 'Berlin Energy Consumption', style: {color: '#2c3e50', fontSize: '16px'}},
  time: {timezone: 'Europe/Berlin'},
  xAxis: {
    type: 'datetime',
    labels: {style: {color: '#7f8c8d'}},
    title: {text: 'Time (CET)', style: {color: '#34495e'}}
  },
  yAxis: {
    title: {text: 'kW', style: {color: '#34495e'}},
    labels: {style: {color: '#7f8c8d'}}
  },
  series: [{
    name: 'Berlin Facility',
    data: [
      [Date.UTC(2017,0,1,0), 35.1], [Date.UTC(2017,0,1,1), 42.3],
      [Date.UTC(2017,0,1,2), 58.9], [Date.UTC(2017,0,1,3), 71.2],
      [Date.UTC(2017,0,1,4), 89.5], [Date.UTC(2017,0,1,5), 104.8],
      [Date.UTC(2017,0,1,6), 132.4], [Date.UTC(2017,0,1,7), 158.9],
      [Date.UTC(2017,0,1,8), 142.1], [Date.UTC(2017,0,1,9), 127.6],
      [Date.UTC(2017,0,1,10), 108.3], [Date.UTC(2017,0,1,11), 92.7]
    ],
    color: '#3498db',
    type: 'spline'
  }],
  legend: {align: 'right', verticalAlign: 'top'},
  plotOptions: {series: {marker: {enabled: false}}}
});

