visualization_code.JavaScript:
Highcharts.chart('container', {
    chart: {
        type: 'line'
    },
    title: {
        text: 'Monthly Performance Metrics with Prognosis Zone',
        style: {
            fontSize: '18px',
            color: '#2c3e50'
        }
    },
    xAxis: {
        categories: ['Jan','Feb','Mar','Apr','May','Jun','Jul','Aug','Sep','Oct','Nov','Dec'],
        labels: {
            style: {
                color: '#7f8c8d'
            }
        },
        lineColor: '#bdc3c7'
    },
    yAxis: {
        title: {
            text: 'Metric Value',
            style: {
                color: '#7f8c8d'
            }
        },
        labels: {
            style: {
                color: '#7f8c8d'
            }
        },
        gridLineColor: '#ecf0f1'
    },
    series: [{
        name: 'Performance Index',
        data: [29.9,71.5,106.4,129.2,144.0,176.0,135.6,148.5,216.4,194.1,95.6,54.4],
        zoneAxis: 'x',
        zones: [{
            value: 8
        }, {
            dashStyle: 'dot',
            color: '#e74c3c'
        }],
        marker: {
            symbol: 'circle',
            radius: 4
        }
    }],
    legend: {
        align: 'right',
        verticalAlign: 'top'
    },
    plotOptions: {
        series: {
            color: '#3498db',
            lineWidth: 3
        }
    }
});
