visualization_code.JavaScript:
Highcharts.chart('container', {
    title: {
        text: 'Browser Market Share (2023 Q3)',
        style: { color: '#1a237e', fontSize: '20px' }
    },
    xAxis: {
        type: 'category',
        labels: {
            style: { color: '#4a148c', fontWeight: 'bold' }
        }
    },
    yAxis: {
        max: 100,
        labels: {
            format: '{value}%',
            style: { color: '#004d40' }
        }
    },
    tooltip: { pointFormat: '{series.name}: <b>{point.y:.1f}%</b>' },
    plotOptions: {
        column: {
            colorByPoint: true,
            dataLabels: { enabled: true, style: { textOutline: 'none' } }
        }
    },
    series: [{
        type: 'column',
        name: 'Market Share',
        keys: ['name', 'y', 'dataLabels.style.visibility'],
        dataLabels: {
            enabled: true,
            style: { visibility: 'hidden', fontSize: '12px' }
        },
        data: [
            ['Chrome', 42.1, 'visible'],
            ['Safari', 18.9],
            ['Edge', 8.7, 'visible'],
            ['Firefox', 7.5],
            ['Samsung Internet', 5.2],
            ['Opera', 3.1],
            ['Brave', 2.8],
            ['UC Browser', 2.5],
            ['Vivaldi', 1.9],
            ['Others', 6.3]
        ],
        colors: ['#4285F4', '#DB4437', '#F4B400', '#0F9D58', '#AB47BC', '#FF7043', '#26A69A', '#7CB342', '#8E24AA', '#78909C']
    }],
    legend: { enabled: false },
    credits: { enabled: false }
});
