visualization_code.JavaScript:
Highcharts.chart('container', {
    chart: {
        type: 'column',
        backgroundColor: '#f8f9fa',
        margin: [40, 25, 65, 60]
    },
    title: {
        text: 'Quarterly Revenue Comparison (2021-2023)',
        style: {
            fontSize: '18px',
            color: '#2c3e50',
            fontWeight: 'bold'
        }
    },
    xAxis: {
        categories: ['Q1', 'Q2', 'Q3', 'Q4'],
        labels: {
            style: {
                color: '#34495e',
                fontSize: '14px'
            }
        },
        title: {
            text: 'Quarter',
            style: {
                color: '#7f8c8d',
                fontSize: '14px'
            }
        }
    },
    yAxis: {
        title: {
            text: 'Revenue (M USD)',
            style: {
                color: '#7f8c8d',
                fontSize: '14px'
            }
        },
        labels: {
            style: {
                color: '#34495e',
                fontSize: '12px'
            },
            format: '{value:.1f}'
        }
    },
    plotOptions: {
        column: {
            grouping: false,
            pointPadding: 0.25,
            borderWidth: 0
        }
    },
    series: [{
        name: '2023',
        data: [4.2, 4.8, 5.1, 6.3],
        color: '#3498db',
        legendIndex: 1
    }, {
        name: '2022',
        data: [3.9, 4.5, 4.8, 5.9],
        color: '#2ecc71',
        legendIndex: 2
    }, {
        name: '2021',
        data: [3.5, 4.1, 4.3, 5.2],
        color: '#e67e22',
        legendIndex: 3
    }],
    legend: {
        align: 'right',
        verticalAlign: 'bottom',
        itemStyle: {
            color: '#2c3e50',
            fontSize: '12px'
        }
    }
});
