visualization_code.JavaScript:
Highcharts.chart('container', {
    chart: {
        type: 'column',
        backgroundColor: '#FFFFFF'
    },
    title: {
        text: 'Spectral Color Distribution Analysis',
        style: { fontSize: '18px', color: '#333333' }
    },
    xAxis: {
        categories: ['Red','Orange','Yellow','Green','Blue','Indigo','Violet','Pink','Brown','Grey'],
        labels: {
            style: { color: 'contrast', fontSize: '12px' },
            useHTML: true,
            formatter: function() {
                return `<span style="color:${this.value.toLowerCase()}">${this.value}</span>`
            }
        }
    },
    yAxis: {
        title: { text: 'Frequency (%)', style: { fontSize: '12px', color: '#666666' } },
        gridLineColor: '#EEEEEE'
    },
    series: [{
        name: 'Color Occurrence',
        data: [
            { name: 'Red', y: 28, color: '#FF0000' },
            { name: 'Orange', y: 37, color: '#FFA500' },
            { name: 'Yellow', y: 38, color: '#FFFF00' },
            { name: 'Green', y: 47, color: '#008000' },
            { name: 'Blue', y: 52, color: '#0000FF' },
            { name: 'Indigo', y: 58, color: '#4B0082' },
            { name: 'Violet', y: 62, color: '#EE82EE' },
            { name: 'Pink', y: 63, color: '#FFC0CB' },
            { name: 'Brown', y: 72, color: '#A52A2A' },
            { name: 'Grey', y: 76, color: '#808080' }
        ],
        dataLabels: {
            enabled: true,
            format: '{y:.0f}%',
            color: '#333333'
        }
    }],
    plotOptions: {
        column: {
            pointPadding: 0.1,
            borderWidth: 0,
            groupPadding: 0.15
        }
    },
    legend: { enabled: false },
    tooltip: {
        headerFormat: '<span style="color:{point.color}">●</span> {series.name}<br/>',
        pointFormat: '<b>{point.category}</b>: {point.y:.1f}%'
    }
});
