visualization_code:
Highcharts.chart('container', {
    chart: {
        type: 'areaspline'
    },
    title: {
        text: 'Environmental Metric Fluctuations (+50% YTD)',
        style: {
            color: '#2c3e50',
            fontSize: '18px'
        }
    },
    xAxis: {
        type: 'datetime',
        labels: {
            style: {
                color: '#7b7b7b'
            }
        }
    },
    yAxis: {
        title: {
            text: 'Measurement Value',
            style: {
                color: '#7b7b7b'
            }
        },
        min: -10,
        gridLineColor: '#f0f0f0'
    },
    series: [{
        name: 'Ecological Index',
        threshold: -10,
        data: [
            [Date.UTC(2023,0), -10],
            [Date.UTC(2023,1), -5.2],
            [Date.UTC(2023,2), 1.8],
            [Date.UTC(2023,3), 6.4],
            [Date.UTC(2023,4), 11.7],
            [Date.UTC(2023,5), 15.3],
            [Date.UTC(2023,6), 12.1],
            [Date.UTC(2023,7), 9.8],
            [Date.UTC(2023,8), 4.6],
            [Date.UTC(2023,9), -1.3],
            [Date.UTC(2023,10), -5.0]
        ],
        zones: [{
            value: 0,
            color: '#f15c80',
            fillColor: '#f7a35c'
        }, {
            value: 10,
            color: '#7cb5ec'
        }, {
            color: '#90ed7d'
        }]
    }],
    plotOptions: {
        areaspline: {
            fillOpacity: 0.3
        }
    },
    legend: {
        align: 'right',
        verticalAlign: 'top'
    }
});
