visualization_code.CSS:
#container { height: 300px; min-width: 310px; max-width: 800px; }
.highcharts-demo-button { background: #f2f2f2; border: none; border-radius: 4px; cursor: pointer; display: inline-block; font-size: 0.8rem; padding: 0.5rem 1.5rem; margin: 0.5rem -5px 0.5rem 10px; transition: background 250ms; }
.highcharts-demo-button:hover { background: #e6e6e6; }

visualization_code.JavaScript:
const chart = Highcharts.chart('container', {
  chart: { type: 'spline' },
  title: { text: 'Renewable Energy Production (2023-2024)' },
  xAxis: {
    type: 'datetime',
    labels: { style: { color: '#666', fontSize: '12px' } }
  },
  yAxis: {
    title: { text: 'Megawatt Hours', style: { color: '#666', fontSize: '12px' } },
    labels: { style: { color: '#666', fontSize: '12px' } }
  },
  plotOptions: {
    spline: { marker: { enabled: false } }
  },
  series: [{
    name: 'Solar',
    data: [
      [Date.UTC(2023,0), 4200], [Date.UTC(2023,1), 4350], [Date.UTC(2023,2), 5100],
      [Date.UTC(2023,3), 5800], [Date.UTC(2023,4), 6200], [Date.UTC(2023,5), 6500],
      [Date.UTC(2023,6), 6700], [Date.UTC(2023,7), 6400], [Date.UTC(2023,8), 5900],
      [Date.UTC(2023,9), 5200], [Date.UTC(2023,10), 4500], [Date.UTC(2023,11), 4000],
      [Date.UTC(2024,0), 4400], [Date.UTC(2024,1), 4600]
    ],
    color: '#FFC107'
  }, {
    name: 'Wind',
    data: [
      [Date.UTC(2023,0), 5800], [Date.UTC(2023,1), 6000], [Date.UTC(2023,2), 5900],
      [Date.UTC(2023,3), 5700], [Date.UTC(2023,4), 5500], [Date.UTC(2023,5), 5300],
      [Date.UTC(2023,6), 5400], [Date.UTC(2023,7), 5600], [Date.UTC(2023,8), 5900],
      [Date.UTC(2023,9), 6200], [Date.UTC(2023,10), 6500], [Date.UTC(2023,11), 6800],
      [Date.UTC(2024,0), 7000], [Date.UTC(2024,1), 7200]
    ],
    color: '#2196F3'
  }],
  responsive: {
    rules: [{
      condition: { maxWidth: 500 },
      chartOptions: {
        legend: { align: 'center', verticalAlign: 'bottom', layout: 'horizontal' }
      }
    }]
  }
});

document.getElementById('small').addEventListener('click', () => {
  chart.setSize(400, 300);
});

document.getElementById('large').addEventListener('click', () => {
  chart.setSize(600, 300);
});
