visualization_code:
Highcharts.chart('container', {
    title: {
        text: 'Visitors to the International Space Station by Country'
    },
    subtitle: {
        text: 'Source: <a href="https://www.nasa.gov/feature/visitors-to-the-station-by-country/">NASA</a>'
    },
    accessibility: {
        typeDescription: 'Sunburst chart with 2 levels. The inner level represents continents, and the outer level represents countries.',
        point: {
            valueSuffix: ' visitors'
        }
    },
    series: [{
        type: 'sunburst',
        name: 'Total',
        data: [
            {id: '1', name: 'America', color: '#efefef'},
            {id: '2', name: 'Asia', color: '#b0b0b0'},
            {id: '3', name: 'Europe', color: '#7b7b7b'},
            {id: '4', name: 'OTH', color: '#000000'},
            {parent: '1', name: 'US', value: 151, color: '#ffffff'},
            {parent: '1', name: 'CA', value: 8, color: '#ffffff'},
            {parent: '1', name: 'BR', value: 1, color: '#ffffff'},
            {parent: '2', name: 'RU', value: 49, color: '#e3e3e3'},
            {parent: '2', name: 'JP', value: 9, color: '#d2d2d2'},
            {parent: '2', name: 'OTH', value: 3, color: '#c1c1c1'},
            {parent: '3', name: 'IT', value: 5, color: '#aeaeae'},
            {parent: '3', name: 'FR', value: 4, color: '#a1a1a1'},
            {parent: '3', name: 'UK', value: 3, color: '#949494'},
            {parent: '3', name: 'OTH', value: 6, color: '#878787'},
            {parent: '4', name: 'OTH', value: 2, color: '#333333'}
        ],
        borderWidth: 1.5,
        borderColor: '#000000',
        dataLabels: {
            format: '{point.name}',
            color: '#000000',
            style: { textOutline: false }
        },
        levels: [{
            level: 1,
            levelIsConstant: false,
            dataLabels: {
                filter: {
                    property: 'outerArcLength',
                    operator: '>',
                    value: 64
                }
            }
        }, {
            level: 2,
            colorByPoint: true
        }]
    }],
    tooltip: {
        headerFormat: '',
        pointFormat: '<b>{point.name}</b>: <b>{point.value}</b> visitors'
    }
});
