visualization_code.JavaScript:
Highcharts.chart('container', {
    chart: { type: 'area', inverted: true },
    title: { text: 'Financial Performance Zones', style: { color: '#333', fontSize: '18px' } },
    xAxis: { 
        type: 'datetime',
        reversed: false,
        labels: { style: { color: '#666', fontSize: '12px' } },
        title: { text: 'Quarter', style: { color: '#666', fontSize: '14px' } }
    },
    yAxis: {
        reversed: false,
        labels: { style: { color: '#666', fontSize: '12px' } },
        title: { text: 'Revenue (USD Millions)', style: { color: '#666', fontSize: '14px' } }
    },
    plotOptions: {
        area: {
            marker: { enabled: false },
            zoneAxis: 'x',
            fillOpacity: 0.5
        }
    },
    series: [{
        name: 'Corporate Revenue',
        data: [
            [Date.UTC(2022, 0), 85],
            [Date.UTC(2022, 3), 112],
            [Date.UTC(2023, 0), 92],
            [Date.UTC(2023, 3), 128],
            [Date.UTC(2024, 0), 105],
            [Date.UTC(2024, 3), 142],
            [Date.UTC(2025, 0), 118],
            [Date.UTC(2025, 3), 155]
        ],
        zones: [{
            value: Date.UTC(2023, 6),
            color: 'rgba(255, 99, 71, 0.6)',
            dashStyle: 'Dash'
        }, {
            color: 'rgba(144, 238, 144, 0.6)'
        }],
        lineWidth: 3,
        color: 'rgba(144, 238, 144, 0.8)',
        negativeColor: 'rgba(255, 99, 71, 0.8)'
    }],
    legend: {
        align: 'right',
        verticalAlign: 'top',
        itemStyle: { color: '#444', fontSize: '12px' }
    }
});
