* {
  font-family: Helvetica, Arial, sans-serif;
}

.container {
  height: 350px;
  width: 500px;
  margin: 0 auto;
}

.highcharts-name-line1,
.highcharts-arrows {
  marker-end: url(#head);
  marker-start: url(#tail);
}

.highcharts-name-line2 {
  marker-start: url(#tail);
}

visualization_code.JavaScript:
const mapOptions = {
  title: {
    text: 'Australian Regional Distribution',
    style: { color: '#333', fontSize: '20px' }
  },
  subtitle: {
    text: 'Source: Synthetic regional data | Base map: Highcharts',
    style: { color: '#666' }
  },
  mapNavigation: {
    enabled: true,
    buttonOptions: { verticalAlign: 'bottom' }
  },
  colorAxis: { min: 0, max: 9, labels: { style: { color: '#444' } } },
  series: [{
    data: [
      {'hc-key': 'au-nt', value: 2.3},
      {'hc-key': 'au-wa', value: 8.1},
      {'hc-key': 'au-ct', value: 4.7},
      {'hc-key': 'au-sa', value: 5.5},
      {'hc-key': 'au-ql', value: 6.9},
      {'hc-key': 'au-2557', value: 3.2},
      {'hc-key': 'au-ts', value: 1.8},
      {'hc-key': 'au-jb', value: 7.4},
      {'hc-key': 'au-ns', value: 9.0},
      {'hc-key': 'au-vi', value: 4.1}
    ],
    mapData: Highcharts.maps['countries/au/au-all'],
    joinBy: 'hc-key',
    name: 'Development Index',
    dataLabels: {
      enabled: true,
      format: '{point.name}',
      style: { fontWeight: 'bold' }
    }
  }, {
    type: 'mappoint',
    colorAxis: false,
    name: 'Regional Hubs',
    data: [{
      name: 'Darwin',
      lat: -12.4634,
      lon: 130.8456,
      dataLabels: { y: 20, style: { color: '#31708f' } }
    }, {
      name: 'Perth',
      lat: -31.9505,
      lon: 115.8605,
      dataLabels: { style: { color: '#8a6d3b' } }
    }]
  }, {
    type: 'mapline',
    color: '#606060',
    lineWidth: 2,
    data: [{
      name: 'Supply Route',
      path: 'M 130.8456 -12.4634 L 115.8605 -31.9505',
      lineWidth: 3
    }]
  }]
};

const lineOptions = {
  title: { text: 'Quarterly Trend Analysis', style: { color: '#333' } },
  xAxis: {
    categories: ['Q1', 'Q2', 'Q3', 'Q4'],
    labels: { style: { color: '#666' } }
  },
  yAxis: {
    title: { text: 'Performance Index', style: { color: '#666' } },
    labels: { style: { color: '#666' } }
  },
  series: [{
    name: 'Western Region',
    data: [8.1, 7.9, 8.4, 8.6],
    color: '#8a6d3b',
    marker: { enabled: false }
  }, {
    name: 'Eastern Region',
    data: [4.7, 5.1, 5.8, 6.3],
    color: '#31708f',
    marker: { enabled: false }
  }]
};

Highcharts.mapChart('map-container', mapOptions);
Highcharts.chart('line-container', lineOptions, chart => {
  chart.series[0].graph.addClass('highcharts-arrows');
});
