visualization_code.JavaScript:
Highcharts.chart('container', {
    chart: {
        type: 'spline',
        backgroundColor: '#FFFFFF',
        borderWidth: 1,
        margin: [30, 45, 70, 60]
    },
    title: {
        text: 'Monthly Sales Performance (2023)',
        style: {
            fontSize: '20px',
            color: '#333333'
        }
    },
    xAxis: {
        categories: ['Jan','Feb','Mar','Apr','May','Jun',
                    'Jul','Aug','Sep','Oct','Nov','Dec'],
        labels: {
            style: {
                fontSize: '12px',
                color: '#666666'
            }
        },
        title: {
            text: 'Month',
            style: {
                fontSize: '14px',
                color: '#333333'
            }
        }
    },
    yAxis: {
        title: {
            text: 'Sales (USD thousands)',
            style: {
                fontSize: '14px',
                color: '#333333'
            }
        },
        labels: {
            style: {
                fontSize: '12px',
                color: '#666666'
            },
            format: '{value}K'
        }
    },
    tooltip: {
        shared: true,
        valueSuffix: 'K',
        headerFormat: '<b>{point.x}</b><br>'
    },
    legend: {
        align: 'center',
        verticalAlign: 'bottom',
        layout: 'horizontal'
    },
    plotOptions: {
        spline: {
            marker: {
                enabled: true,
                radius: 4
            }
        }
    },
    series: [{
        name: 'North Region',
        data: [185, 198, 214, 231, 245, 263, 
               241, 227, 208, 192, 179, 205],
        color: '#2E8B57'
    }, {
        name: 'South Region',
        data: [225, 210, 195, 182, 198, 215, 
               238, 254, 269, 281, 262, 247],
        color: '#4682B4'
    }]
});
