visualization_code:
Highcharts.chart('container', {
    chart: {
        type: 'line',
        zooming: { type: 'x' }
    },
    title: { text: 'Synthetic Metric Analysis with Annotations' },
    subtitle: { text: 'Demonstrating coordinate-based annotations' },
    xAxis: {
        tickInterval: 0.5,
        gridLineWidth: 1,
        title: { text: 'Measurement Interval', style: { color: '#333', fontSize: '12px' } }
    },
    yAxis: {
        title: { text: 'Metric Value', style: { color: '#333', fontSize: '12px' } }
    },
    series: [{
        name: 'Synthetic Dataset',
        data: [29.9, 71.5, 106.4, 129.2, 144.0, 176.0, 135.6, 148.5, 216.4, 194.1, 95.6, 54.4],
        color: '#4E79A7'
    }],
    annotations: [{
        labels: [{
            point: { x: 3, y: 129.2, xAxis: 0, yAxis: 0 },
            text: 'Peak: {y:.1f}'
        }, {
            point: { x: 8, y: 216.4, xAxis: 0, yAxis: 0 },
            text: 'Max: {y:.1f}'
        }],
        labelOptions: {
            backgroundColor: 'rgba(255,255,255,0.9)',
            borderColor: '#4E79A7'
        }
    }],
    plotOptions: {
        series: { marker: { radius: 3 } }
    }
});
