let minMax = {};

function getMinMax(chart) {
  const yData = chart.series[0].processedYData;
  minMax = {
    yMin: Math.min(...yData),
    yMax: Math.max(...yData),
    xMin: chart.series[0].xData[yData.indexOf(minMax.yMin)],
    xMax: chart.series[0].xData[yData.indexOf(minMax.yMax)]
  };
}

Highcharts.chart('container', {
  chart: {
    type: 'spline',
    zooming: { type: 'x' },
    backgroundColor: '#ffffff'
  },
  title: { 
    text: 'Monthly Operational Metrics (2023)',
    style: { fontSize: '20px', color: '#2c3e50' }
  },
  xAxis: {
    type: 'datetime',
    labels: { format: '{value:%b}', style: { color: '#7f8c8d' } }
  },
  yAxis: {
    title: { text: 'Performance Index', style: { color: '#7f8c8d' } },
    labels: { format: '{value}', style: { color: '#7f8c8d' } }
  },
  annotations: [{
    shapes: [{
      type: 'path',
      points: [
        function(annotation) {
          getMinMax(annotation.chart);
          return { x: minMax.xMin, xAxis: 0, y: (minMax.yMin + minMax.yMax)/2, yAxis: 0 };
        },
        function(annotation) {
          return { x: minMax.xMax, xAxis: 0, y: (minMax.yMin + minMax.yMax)/2, yAxis: 0 };
        }
      ]
    }],
    labels: [{
      point: function() { return { x: minMax.xMax, y: minMax.yMax }; },
      format: 'Peak: {y:.1f}', style: { color: '#e74c3c' }
    }, {
      point: function() { return { x: minMax.xMin, y: minMax.yMin }; },
      format: 'Low: {y:.1f}', style: { color: '#2ecc71' }
    }]
  }],
  series: [{
    name: 'Process Efficiency',
    color: '#3498db',
    data: [
      [Date.UTC(2023,0), 68.3], [Date.UTC(2023,1), 72.1], [Date.UTC(2023,2), 85.4],
      [Date.UTC(2023,3), 78.9], [Date.UTC(2023,4), 92.5], [Date.UTC(2023,5), 88.2],
      [Date.UTC(2023,6), 95.1], [Date.UTC(2023,7), 81.7], [Date.UTC(2023,8), 76.4],
      [Date.UTC(2023,9), 89.9], [Date.UTC(2023,10), 94.3], [Date.UTC(2023,11), 83.6]
    ]
  }],
  legend: {
    layout: 'horizontal',
    align: 'center',
    verticalAlign: 'bottom'
  }
});
