events.render:
function() {
  const chart = this;
  const sdColors = ['#FFAAAA', '#FFD700', '#90EE90'];
  const zones = [
    { value: -3, color: sdColors[0] },
    { value: -2, color: sdColors[1] },
    { value: -1, color: sdColors[2] },
    { value: 1, color: sdColors[2] },
    { value: 2, color: sdColors[1] },
    { value: 3, color: sdColors[0] }
  ];
  chart.series[0].update({ zones: zones });
}
