events.render:
function() {
const chart=this,zones=[],renderer=chart.renderer,sdColors=[Highcharts.color('#4572A7').brighten(0.3).get(),Highcharts.color('#4572A7').brighten(0.15).get(),'#4572A7'];
chart.series.forEach(s=>{if(s.type==='bellcurve'){
const xAxis=s.xAxis,zoneStops=[-3,-2,-1,0,1,2,3],base=1;
if(!s.options.zones){zoneStops.forEach((v,i)=>{zones.push({value:v,color:i<3?sdColors[i]:sdColors[5-i]})});s.update({zones:zones})}
if(!s.stDevLabelsGroup){
const percents=['2.1%','13.6%','34.1%','34.1%','13.6%','2.1%'];
s.stDevLabelsGroup=renderer.g().attr({zIndex:5}).add();
s.stDevLabels=[];
zoneStops.forEach((v,i)=>{if(i>0){
const label=renderer.label(percents[i-1],0,0).add(s.stDevLabelsGroup);
label.attr({x:xAxis.toPixels((zoneStops[i]+zoneStops[i-1])/2)-(label.width/2),y:chart.plotHeight+chart.plotTop-20});
s.stDevLabels.push(label)}})}}})}
