Highcharts.chart('container', {
  chart: { type: 'spline' },
  title: { text: '2023 Website Traffic Analysis', style: { color: '#2F4F4F', fontSize: '20px' } },
  xAxis: {
    type: 'datetime',
    labels: { format: '{value:%b}', style: { color: '#556B2F' } },
    title: { text: 'Month', style: { fontSize: '14px', color: '#4B5320' } }
  },
  yAxis: {
    title: { text: 'Visitors (thousands)', style: { fontSize: '14px', color: '#4B5320' } },
    labels: { format: '{value}k', style: { color: '#556B2F' } }
