      [Date.UTC(2023,5), 27.9],
      [Date.UTC(2023,6), 24.6],
      [Date.UTC(2023,7), 23.8],
      [Date.UTC(2023,8), 25.3],
      [Date.UTC(2023,9), 26.7],
      [Date.UTC(2023,10), 28.4],
      [Date.UTC(2023,11), 32.6]
    ]
  }],
  legend: { itemStyle: { color: '#2F4F4F' } },
  plotOptions: { spline: { marker: { enabled: false } } }
});
