visualization_code.javascript:
Highcharts.chart('energy-container', {
  chart: { type: 'pie' },
  title: { text: 'Global Energy Production Sources (2023)' },
  colors: ['#666666', '#2596BE', '#7FDBFF', '#2ECC40', '#FFDC00', '#FF851B', '#B10DC9', '#FF4136', '#85144b', '#3D9970'],
  plotOptions: {
    pie: {
      dataLabels: { format: '{point.name}: {y}%' },
      showInLegend: true
    }
  },
  legend: {
    events: {
      itemClick: function() {
        return confirm('Toggle visibility of this energy source?') || false;
      }
    }
  },
  series: [{
    name: 'Energy Share',
    data: [
      ['Coal', 35], ['Natural Gas', 25], ['Hydro', 15],
      ['Wind', 10], ['Solar', 8], ['Nuclear', 5],
      ['Biomass', 1], ['Geothermal', 0.5], ['Oil', 0.3],
      ['Other Renewables', 0.2]
    ]
  }],
  tooltip: { pointFormat: '{point.y}% of global production' }
});
