visualization_code.JavaScript:
Highcharts.chart('container', {
    chart: {
        type: 'spline',
        marginRight: 40
    },
    title: {
        text: 'Monthly Sales Performance: 2022-2023',
        style: {
            fontSize: '18px',
            color: '#2C3E50'
        }
    },
    xAxis: {
        type: 'datetime',
        labels: {
            format: '{value:%b %Y}',
            rotation: -45
        }
    },
    yAxis: [{
        title: {
            text: 'Revenue ($)',
            style: {
                color: '#2980B9'
            }
        },
        labels: {
            format: '${value:,.0f}K',
            style: {
                color: '#2980B9'
            }
        }
    }, {
        title: {
            text: 'Units Sold',
            style: {
                color: '#27AE60'
            }
        },
        opposite: true,
        labels: {
            format: '{value:,.0f}',
            style: {
                color: '#27AE60'
            }
        }
    }],
    series: [{
        name: 'Revenue',
        color: '#2980B9',
        yAxis: 0,
        data: [
            [Date.UTC(2022,0), 215], [Date.UTC(2022,1), 228], [Date.UTC(2022,2), 198],
            [Date.UTC(2022,3), 245], [Date.UTC(2022,4), 272], [Date.UTC(2022,5), 309],
            [Date.UTC(2022,6), 294], [Date.UTC(2022,7), 331], [Date.UTC(2022,8), 357],
            [Date.UTC(2022,9), 382], [Date.UTC(2022,10), 405], [Date.UTC(2022,11), 433],
            [Date.UTC(2023,0), 458], [Date.UTC(2023,1), 476], [Date.UTC(2023,2), 419],
            [Date.UTC(2023,3), 487], [Date.UTC(2023,4), 513], [Date.UTC(2023,5), 542],
            [Date.UTC(2023,6), 527], [Date.UTC(2023,7), 561], [Date.UTC(2023,8), 588],
            [Date.UTC(2023,9), 614], [Date.UTC(2023,10), 637], [Date.UTC(2023,11), 665]
        ]
    }, {
        name: 'Units Sold',
        color: '#27AE60',
        yAxis: 1,
        data: [
            [Date.UTC(2022,0), 520], [Date.UTC(2022,1), 575], [Date.UTC(2022,2), 490],
            [Date.UTC(2022,3), 632], [Date.UTC(2022,4), 710], [Date.UTC(2022,5), 825],
            [Date.UTC(2022,6), 787], [Date.UTC(2022,7), 894], [Date.UTC(2022,8), 952],
            [Date.UTC(2022,9), 1015], [Date.UTC(2022,10), 1082], [Date.UTC(2022,11), 1150],
            [Date.UTC(2023,0), 1223], [Date.UTC(2023,1), 1295], [Date.UTC(2023,2), 1138],
            [Date.UTC(2023,3), 1314], [Date.UTC(2023,4), 1390], [Date.UTC(2023,5), 1475],
            [Date.UTC(2023,6), 1422], [Date.UTC(2023,7), 1518], [Date.UTC(2023,8), 1593],
            [Date.UTC(2023,9), 1660], [Date.UTC(2023,10), 1735], [Date.UTC(2023,11), 1812]
        ]
    }],
    tooltip: {
        shared: true,
        headerFormat: '<b>{point.x:%B %Y}</b><br>',
        pointFormat: '{series.name}: ' +
            '<span style="color:{point.color}">' +
            '{#if series.yAxis==0}${point.y:,.0f}K{else}{point.y:,.0f}{/if}</span>'
    },
    legend: {
        align: 'center',
        verticalAlign: 'bottom'
    }
});
