visualization_code.javascript:
Highcharts.chart('container', {
    chart: {
        type: 'networkgraph',
        backgroundColor: '#f8f9fa'
    },
    title: {
        text: 'Indo-European Language Evolution',
        style: { fontSize: '1.4em', color: '#2c3e50' }
    },
    plotOptions: {
        networkgraph: {
            keys: ['from', 'to'],
            layoutAlgorithm: {
                enableSimulation: true,
                friction: -0.9,
                linkLength: 150
            },
            dataLabels: {
                enabled: true,
                format: '{point.name}',
                style: { textOutline: 'none' }
            }
        }
    },
    series: [{
        nodes: [
            { id: 'PIE', name: 'Proto-Indo-European', color: '#e74c3c', marker: { radius: 28 } },
            { id: 'BS', name: 'Balto-Slavic', color: '#3498db' },
            { id: 'GER', name: 'Germanic', color: '#2ecc71' },
            { id: 'CEL', name: 'Celtic', color: '#f1c40f' },
            { id: 'ITA', name: 'Italic', color: '#9b59b6' },
            { id: 'HEL', name: 'Hellenic', color: '#e67e22' },
            { id: 'IIR', name: 'Indo-Iranian', color: '#1abc9c' },
            { id: 'LAT', name: 'Latin', color: '#9b59b6', marker: { radius: 16 } },
            { id: 'SAN', name: 'Sanskrit', color: '#1abc9c', marker: { radius: 16 } },
            { id: 'GRM', name: 'Proto-Germanic', color: '#2ecc71', marker: { radius: 16 } }
        ],
        data: [
            ['PIE', 'BS'], ['PIE', 'GER'], ['PIE', 'CEL'],
            ['PIE', 'ITA'], ['PIE', 'HEL'], ['PIE', 'IIR'],
            ['ITA', 'LAT'], ['IIR', 'SAN'], ['GER', 'GRM']
        ]
    }],
    legend: {
        enabled: false
    }
});
