visualization_code.javascript:
Highcharts.chart('container', {
  chart: { type: 'column', events: { drilldown: e => e.seriesOptions.type = 'pie' } },
  title: { text: 'Product Category Sales with Subcategory Breakdown', style: { fontSize: '20px', color: '#2F4858' } },
  xAxis: { type: 'category', labels: { style: { fontSize: '14px', fontWeight: 'bold', color: '#35495E' } } },
  yAxis: { title: { text: 'Total Units', style: { fontSize: '14px', color: '#35495E' } }, labels: { format: '{value} units' } },
  plotOptions: {
    column: { dataLabels: { enabled: true, format: '{point.y} units' } },
    pie: { allowPointSelect: true, cursor: 'pointer', dataLabels: { format: '<b>{point.name}</b>: {point.percentage:.1f}%' } }
  },
  series: [{
    name: 'Categories',
    colorByPoint: true,
    data: [
      { name: 'Electronics', y: 45, drilldown: 'electronics', color: '#4B88A2' },
      { name: 'Apparel', y: 30, drilldown: 'apparel', color: '#BB7E5D' },
      { name: 'Home & Garden', y: 25, drilldown: 'home_garden', color: '#8A9B68' }
    ]
  }],
  drilldown: {
    activeDataLabelStyle: { color: '#35495E', fontWeight: 'normal' },
    series: [
      {
        id: 'electronics',
        name: 'Electronics Subcategories',
        data: [
          { name: 'Smartphones', y: 15, color: '#3B6E88' },
          { name: 'Laptops', y: 10, color: '#4B88A2' },
          { name: 'Tablets', y: 8, color: '#5BA2BF' },
          { name: 'Wearables', y: 7, color: '#6DBCDB' },
          { name: 'Accessories', y: 5, color: '#8FD4F2' }
        ]
      },
      {
        id: 'apparel',
        name: 'Apparel Subcategories',
        data: [
          { name: "Men's Clothing", y: 12, color: '#A56B4D' },
          { name: "Women's Clothing", y: 10, color: '#BB7E5D' },
          { name: 'Footwear', y: 5, color: '#D19470' },
          { name: 'Accessories', y: 3, color: '#E8AC86' }
        ]
      },
      {
        id: 'home_garden',
        name: 'Home & Garden Subcategories',
        data: [
          { name: 'Furniture', y: 10, color: '#798D54' },
          { name: 'Decor', y: 8, color: '#8A9B68' },
          { name: 'Kitchenware', y: 5, color: '#9CAE7C' },
          { name: 'Lighting', y: 2, color: '#AEBF8F' }
        ]
      }
    ]
  }
});
