visualization_code.javascript:
Highcharts.chart('revenueContainer', {
  chart: { type: 'column', spacing: [20, 15, 10, 15] },
  title: { text: '2023 Quarterly Revenue by Region', style: { fontSize: '20px', color: '#2c3e50' } },
  accessibility: {
    screenReaderSection: {
      beforeChartFormat: '<h5>{chartTitle}</h5><div>Clustered column chart showing three regional revenue streams across four quarters</div>'
    }
  },
  xAxis: {
    categories: ['Q1', 'Q2', 'Q3', 'Q4'],
    labels: { style: { fontSize: '14px', fontWeight: '600' } }
  },
  yAxis: {
    title: { text: 'Revenue (M USD)', style: { color: '#34495e' } },
    labels: { format: '{value:.1f}M', style: { fontSize: '12px' } }
  },
  plotOptions: {
    column: {
      dataLabels: {
        enabled: true,
        format: '{point.y:.1f}M',
        color: '#2c3e50',
        filter: { property: 'y', operator: '>', value: 5 }
      }
    }
  },
  series: [{
    name: 'North America',
    data: [12.3, 14.1, 16.8, 18.5],
    color: '#3498db'
  }, {
    name: 'Europe',
    data: [8.9, 9.7, 11.2, 10.8],
    color: '#e74c3c'
  }, {
    name: 'Asia-Pacific',
    data: [6.5, 8.3, 10.9, 13.4],
    color: '#2ecc71'
  }],
  legend: { align: 'center', verticalAlign: 'bottom', itemStyle: { fontSize: '13px' } }
});
