document.getElementById('addPoint').addEventListener('click', () => {
  const chart = Highcharts.charts[0];
  const newX = chart.series[0].data.length;
  const newY = Math.floor(Math.random() * (95 - 75 + 1)) + 75;
  chart.series[0].addPoint({x: newX, y: newY}, true, true);
});
