visualization_code.javascript:
Highcharts.chart('energyContainer', {
  chart: {
    type: 'bar',
    backgroundColor: 'transparent'
  },
  title: {
    text: 'Global Renewable Energy Adoption (2023)',
    style: { fontSize: '24px', color: '#2c3e50' }
  },
  xAxis: {
    categories: ['Solar PV', 'Wind Onshore', 'Hydropower', 'Bioenergy', 'Wind Offshore', 'Geothermal', 'Concentrated Solar', 'Tidal', 'Wave', 'Ocean Thermal'],
    labels: { style: { fontSize: '14px', color: '#34495e' } }
  },
  yAxis: {
    title: { text: 'Market Share (%)', style: { color: '#34495e' } },
    labels: { format: '{value}%', style: { color: '#7f8c8d' } }
  },
  plotOptions: {
    bar: {
      dataLabels: {
        enabled: true,
        format: '{y}%',
        color: '#2c3e50',
        style: { textOutline: 'none' }
      }
    }
  },
  series: [{
    name: 'Current Adoption',
    color: '#27ae60',
    data: [
      { y: 28.4, accessibility: { description: 'Dominant technology with 35% annual growth rate' } },
      { y: 22.1, accessibility: { description: 'Mature technology facing land constraints' } },
      { y: 18.7, accessibility: { description: 'Established base load renewable source' } },
      { y: 12.3, accessibility: { description: 'Growing agricultural sector adoption' } },
      { y: 8.9, accessibility: { description: 'High-growth coastal energy solution' } },
      { y: 4.5, accessibility: { description: 'Geologically dependent stable source' } },
      { y: 2.8, accessibility: { description: 'Desert region specialization' } },
      { y: 1.2, accessibility: { description: 'Emerging tidal stream projects' } },
      { y: 0.7, accessibility: { description: 'Experimental coastal installations' } },
      { y: 0.4, accessibility: { description: 'Deep-ocean R&D initiatives' } }
    ]
  }, {
    name: 'Projected Growth (2025)',
    type: 'line',
    color: '#2980b9',
    data: [32.6, 25.4, 19.1, 14.8, 12.3, 5.1, 3.4, 1.8, 1.2, 0.7],
    marker: { symbol: 'diamond', radius: 6 }
  }],
  legend: {
    align: 'right',
    verticalAlign: 'top',
    itemStyle: { color: '#34495e' }
  },
  accessibility: {
    description: 'Comparison of current market shares and projected growth rates for major renewable energy technologies. Solar PV leads adoption while emerging technologies show growth potential.'
  }
});
