visualization_code.JavaScript:
Highcharts.chart('container', {
    chart: { type: 'line' },
    title: { text: '2023 Temperature Trends (Log Scale)' },
    yAxis: {
        type: 'logarithmic',
        title: { text: 'Temperature (°C)', style: { color: '#333', fontSize: '14px' } },
        labels: { style: { color: '#666', fontWeight: 'bold' } }
    },
    xAxis: {
        type: 'datetime',
        labels: { format: '{value:%b}', style: { color: '#444', fontSize: '12px' } }
    },
    series: [{
        name: 'Average Temperature',
        data: [
            [Date.UTC(2023,0), 5], [Date.UTC(2023,1), 8], [Date.UTC(2023,2), 12],
            [Date.UTC(2023,3), 16], [Date.UTC(2023,4), 20], [Date.UTC(2023,5), 25],
            [Date.UTC(2023,6), 28], [Date.UTC(2023,7), 24], [Date.UTC(2023,8), 18],
            [Date.UTC(2023,9), 13], [Date.UTC(2023,10), 9], [Date.UTC(2023,11), 6]
        ],
        color: '#1e90ff',
        marker: { enabled: false }
    },{
        name: 'Record Highs',
        data: [
            [Date.UTC(2023,0), 15], [Date.UTC(2023,1), 18], [Date.UTC(2023,2), 22],
            [Date.UTC(2023,3), 26], [Date.UTC(2023,4), 30], [Date.UTC(2023,5), 35],
            [Date.UTC(2023,6), 38], [Date.UTC(2023,7), 34], [Date.UTC(2023,8), 28],
            [Date.UTC(2023,9), 23], [Date.UTC(2023,10), 19], [Date.UTC(2023,11), 16]
        ],
        color: '#ff4500',
        dashStyle: 'Dash',
        marker: { enabled: false }
    }],
    legend: { align: 'right', verticalAlign: 'top' }
});
