events.unselect:
function() {
  const chart = this.series.chart,
        text = `<strong>${this.category}</strong>: $${this.y}K`;

  if (!chart.unselectLabel) {
    chart.unselectLabel = chart.renderer.label(text, 100, 70)
      .attr({
        padding: 12,
        r: 8,
        fill: '#e74c3c',
        zIndex: 5
      })
      .css({
        color: 'white',
        fontWeight: 'bold'
      })
      .add();
  } else {
    chart.unselectLabel.attr({ text: text });
  }
}
