visualization_code.javascript:
Highcharts.chart('container', {
  chart: { type: 'column', backgroundColor: '#f8f9fa', margin: [40, 40, 80, 75] },
  title: { text: '2023 Synthetic Sales Performance', style: { fontSize: '20px', color: '#2c3e50', fontFamily: 'Arial' } },
  subtitle: { text: 'YTD Growth: +22.4% | μ=158.3k, σ=31.2k', align: 'left' },
  xAxis: { 
    categories: ['Jan','Feb','Mar','Apr','May','Jun','Jul','Aug','Sep','Oct','Nov','Dec'],
    labels: { style: { color: '#34495e', fontSize: '12px' } },
    lineColor: '#bdc3c7'
  },
  yAxis: {
    title: { text: 'Revenue (USD thousands)', style: { color: '#34495e', fontSize: '14px' } },
    labels: { format: '{value:.0f}k', style: { color: '#7f8c8d' } },
    gridLineColor: '#ecf0f1'
  },
  plotOptions: {
    column: {
      color: {
        linearGradient: { x1: 0, x2: 0, y1: 0, y2: 1 },
        stops: [ [0, '#3498db'], [1, '#2980b9'] ]
      },
      pointPadding: 0.1,
      borderWidth: 0
    }
  },
  legend: { enabled: false },
  tooltip: { valueSuffix: 'k USD', headerFormat: '<b>{point.key}</b><br>' },
  series: [{
    name: 'Monthly Revenue',
    data: [
      122, 135, 141, 153, 162, 178,
      165, 159, 188, 204, 219, 234
    ]
  }]
});
