const synthData = [
  { id: 'A0', parent: '', name: 'Nova Prime' },

  // Continents
  { id: 'B1', parent: 'A0', name: 'Cygnus Major', value: 2100000000 },
  { id: 'B2', parent: 'A0', name: 'Vega Minor', value: 1850000000 },
  { id: 'B3', parent: 'A0', name: 'Orion Belt', value: 1620000000 },

  // Cygnus Major Regions
  { id: 'C1', parent: 'B1', name: 'Northern Archipelago' },
  { id: 'C2', parent: 'B1', name: 'Electrum Plains' },

  // Vega Minor Regions
  { id: 'C3', parent: 'B2', name: 'Crimson Wastes' },
  { id: 'C4', parent: 'B2', name: 'Silica Delta' },

  // Orion Belt Regions
  { id: 'C5', parent: 'B3', name: 'Zephyr Highlands' },
  { id: 'C6', parent: 'B3', name: 'Helios Basin' },

  // Synthetic Countries
  { id: 'D1', parent: 'C1', name: 'Nordion', value: 82456789 },
  { id: 'D2', parent: 'C1', name: 'Thalassa', value: 45673211 },
  { id: 'D3', parent: 'C2', name: 'Palladia', value: 67893452 },
  { id: 'D4', parent: 'C2', name: 'Ferrium', value: 32450987 },
  { id: 'D5', parent: 'C3', name: 'Pyros', value: 15432678 },
  { id: 'D6', parent: 'C3', name: 'Scoria', value: 9876543 },
  { id: 'D7', parent: 'C4', name: 'Quartzara', value: 23456789 },
  { id: 'D8', parent: 'C4', name: 'Opalica', value: 17654321 },
  { id: 'D9', parent: 'C5', name: 'Aetheris', value: 43215678 },
  { id: 'D10', parent: 'C5', name: 'Nimbara', value: 29876543 },
  { id: 'D11', parent: 'C6', name: 'Solara', value: 56789012 },
  { id: 'D12', parent: 'C6', name: 'Helion', value: 41234567 }
];

Highcharts.chart('container', {
  chart: { backgroundColor: '#1a1a1a' },
  title: { 
    text: 'Synthetic Planetary Demographics (2347 CE)',
    style: { color: '#e6e6e6', fontSize: '20px' }
  },
  series: [{
    type: 'sunburst',
    data: synthData,
    allowDrillToNode: true,
    levels: [{
      level: 1,
      levelIsConstant: false,
      dataLabels: { 
        color: '#ffffff',
        filter: { property: 'innerArcLength', operator: '>', value: 40 }
      }
    }, {
      level: 2,
      colorByPoint: true,
      colorVariation: { key: 'brightness', to: -0.3 }
    }, {
      level: 3,
      colorVariation: { key: 'brightness', to: 0.5 }
    }]
  }],
  tooltip: {
    formatter: function() {
      return `<b>${this.point.name}</b><br>Population: ${Highcharts.numberFormat(this.point.value, 0)}`;
    }
  },
  colors: ['#4f772d', '#90a955', '#ecf39e', '#132a13', '#31572c', '#68904d']
});
