visualization_code.JavaScript:
Highcharts.chart('engine-gauge', {
  chart: {
    type: 'gauge',
    backgroundColor: '#1a1a1a'
  },
  title: {
    text: 'Turbocharger RPM Monitor',
    style: {
      color: '#ffffff',
      fontSize: '20px'
    }
  },
  pane: {
    startAngle: -120,
    endAngle: 120,
    background: [{
      outerRadius: '110%',
      innerRadius: '90%',
      backgroundColor: Highcharts.color('#333333').setOpacity(0.3).get()
    }]
  },
  yAxis: {
    min: 0,
    max: 8000,
    lineColor: '#666666',
    tickInterval: 1000,
    labels: {
      style: {
        color: '#cccccc',
        fontSize: '14px'
      }
    },
    plotBands: [{
      from: 7000,
      to: 8000,
      color: 'rgba(255, 0, 0, 0.2)',
      thickness: '20%'
    }]
  },
  plotOptions: {
    gauge: {
      dataLabels: {
        format: '{y} RPM',
        style: {
          fontSize: '18px',
          color: '#ffffff'
        }
      },
      pivot: {
        radius: 12,
        borderWidth: 2,
        borderColor: '#ff4444',
        backgroundColor: {
          linearGradient: { x1: 0, y1: 0, x2: 1, y2: 1 },
          stops: [
            [0, '#ff8888'],
            [1, '#cc0000']
          ]
        }
      }
    }
  },
  series: [{
    name: 'RPM',
    data: [6320],
    tooltip: {
      valueSuffix: ' RPM'
    },
    dial: {
      radius: '90%',
      backgroundColor: 'rgba(255, 68, 68, 0.8)',
      baseWidth: 12
    }
  }]
});
